mapper.select:
	select r.*,g.*
  from receiving r,godown g where r.r_whid=g.go_id

          and   r_name like #{rname}


            and r_SKUmodel = #{rskumodel}


           and  r_supplierid = #{rSupplierid}


            and r_crossflag = #{rCrossflag}


            and r_directflag = #{rDirectflag}


            and   r_time >= #{startTime} 


              and r_time <= #{endTime}
